import React, {Component} from 'react';
import './App.css';

class App extends Component {
 state ={
  value : 0
 };

incremento= () => { 
  this.setState({ value : this.state.value + 1 });

}



 render(){   

    const { value}  = this.state;

    return (
      <div className="App">

        <h1>{value}</h1>  
        <button onClick={this.incremento}>+</button>   

      </div>
    );
}

 }


export default App;
